import React from "react";
import { Provider } from "react-redux";
import reducers from "reducers";
import { configureStore } from "@reduxjs/toolkit";

const Root = ({ children, preloadedState = {} }) => {
  return (
    <Provider
      store={configureStore({
        reducer: reducers,
        preloadedState,
      })}
    >
      {children}
    </Provider>
  );
};

export default Root;
